pe":"paragraph","content":[{"nodeType":"text","value":"yarn add express mongoose body-parser morgan cors","marks":[{"type":"code"}],"data":{}}],"data":{}},{"nodeType":"heading-2","content":[{"nodeType":"text","value":"Server.Js","marks":[],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"Create a file called server.js in your project. This file will essentially have all of our server logic hence the name server.js.","marks":[],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"'use strict';

/* Packages */var express = require('express');var bodyParser = require('body-parser');var logger = require('morgan');var mongoose = require('mongoose');var cors = require('cors');var port = process.env.PORT || 8080;var app = express();

/* Connect to Mongo */
mongoose.connect("mongodb://localhost:27017/todos");var db = mongoose.connection;
db.on("error", function (err) {
    console.error("\x1b[31m%s\x1b[0m", "Connection error:", err);});
db.once("open", function () {
    console.log("\x1b[36m%s\x1b[0m", "Connected to Mongo");});

/* Middleware */
app.use(logger("dev")); // logs http requests in console
app.use(bodyParser.json()); // enables us to get data from a post request

// catch 404 and forward to error handler
app.use(function (req, res, next) {
    var err = new Error("Not Found");
    err.status = 404;
    next(err);});

// Error handler
app.use(function (err, req, res, next) {
    res.status(err.status || 500);
    res.json({
        error: {
            message: err.message
        }
    });});

// Start Server
app.listen(port, function () {
    console.log("\x1b[36m%s\x1b[0m", "Server up and running on port:", port);});","marks":[{"type":"code"}],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"Just with any node project, we require our dependencies that we installed earlier at the top of the file. Next we connect to our local MongoDB service and append the name of the database we want to create which is ","marks":[],"data":{}},{"nodeType":"text","value":"/todos","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":".","marks":[],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"Now, in your terminal run: ","marks":[],"data":{}},{"nodeType":"text","value":"nodemon server.js","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":". Make sure you are running MongoDB in the background or else you might receive a console error in your terminal. If everything went smoothly, you should see:","marks":[],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"Server up and running on port: 8080
Connected to Mongo","marks":[{"type":"code"}],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"at the end of your console.","marks":[],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"Notice the ","marks":[],"data":{}},{"nodeType":"text","value":"app.use()","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" methods in the file. These are express’ middleware. They handle events and routes when triggered. Later we will add our ","marks":[],"data":{}},{"nodeType":"text","value":"todos routes middleware","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" into the mix so we can ","marks":[],"data":{}},{"nodeType":"text","value":"create, read, update and delete todos","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":".","marks":[],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"Lastly we have the ","marks":[],"data":{}},{"nodeType":"text","value":"app.listen()","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" method, this basically starts our server at a given port. We set it to the node environment’s port ","marks":[],"data":{}},{"nodeType":"text","value":"or","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" port 8080. You can visit ","marks":[],"data":{}},{"nodeType":"text","value":"localhost:8080","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" in your browser, you should see an error message saying ","marks":[],"data":{}},{"nodeType":"text","value":"not found","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":". We have not set any routes in the root of the server, that’s why it defaulted to use the ","marks":[],"data":{}},{"nodeType":"text","value":"404","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" middleware.","marks":[],"data":{}}],"data":{}},{"nodeType":"heading-2","content":[{"nodeType":"text","value":"Creating Our Todo Model","marks":[],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"In your project directory create a folder called ","marks":[],"data":{}},{"nodeType":"text","value":"Models","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" and then a file called ","marks":[],"data":{}},{"nodeType":"text","value":"Todo.js","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" within it. This file will shape how we want our todos to look. We want a ","marks":[],"data":{}},{"nodeType":"text","value":"title, status and creation_date","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" for every todo. We’ll use the ","marks":[],"data":{}},{"nodeType":"hyperlink","content":[{"nodeType":"text","value":"mongoose","marks":[],"data":{}}],"data":{"uri":"http://mongoosejs.com/docs/guide.html"}},{"nodeType":"text","value":" package to help us achieve this.","marks":[],"data":{}}],"data":{}},{"nodeType":"heading-3","content":[{"nodeType":"text","value":"Models/Todo.Js","marks":[],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"'use strict';

var mongoose = require("mongoose");var Schema = mongoose.Schema;

var TodoSchema = new Schema({
    title: {
        type: String,
        required: true,
        trim: true
    },
    status: {
        type: Boolean,
        required: true,
        default: false
    },
    creation_date: { 
        type: Date,
        required: true, 
        default: Date.now 
    }});

var Todo = mongoose.model('Todo', TodoSchema);
module.exports = Todo; // export `Todo` variable to be available for other files","marks":[{"type":"code"}],"data":{}}],"data":{}},{"nodeType":"heading-2","content":[{"nodeType":"text","value":"Todo Routes","marks":[],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"Now is the time to create our routes and test our endpoints. We’ll be using Express’ router to help us. When we send a ","marks":[],"data":{}},{"nodeType":"text","value":"GET","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" request to ","marks":[],"data":{}},{"nodeType":"text","value":"localhost:8080/api/todos","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":", our goal is to get a list of all the todos from the todos database in json form. When we send a ","marks":[],"data":{}},{"nodeType":"text","value":"POST","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" request to the same endpoint, we want to create a new todo from a parsed json object. Here’s a table to better illustrate what we’re trying to accomplish.","marks":[],"data":{}}],"data":{}},{"nodeType":"embedded-asset-block","content":[],"data":{"target":{"sys":{"id":"2RY14RAMNflLhSRlVn0AjF","type":"Link","linkType":"Asset"}}}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"Create a routes folder, then create a file named Todos.js inside.","marks":[],"data":{}}],"data":{}},{"nodeType":"heading-3","content":[{"nodeType":"text","value":"Routes/Todos.Js","marks":[],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":""use strict";

var express = require("express");var router = express.Router();var mongoose = require('mongoose');

var Todo = require("../models/todo");

router.get('/', function(req, res, next) {
    res.json({message: 'API working!'})});

module.exports = router; // export `router` variable to be available for other files","marks":[{"type":"code"}],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"Import the ","marks":[],"data":{}},{"nodeType":"text","value":"routes/todos.js","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" file near top of server.js file.","marks":[],"data":{}}],"data":{}},{"nodeType":"heading-3","content":[{"nodeType":"text","value":"Server.Js","marks":[],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"var todosRoute = require('./routes/todos.js');","marks":[{"type":"code"}],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"Add a todos route middleware at ","marks":[],"data":{}},{"nodeType":"text","value":"/api/todos","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" just above the 404 error handler.","marks":[],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"// Register 'todos' Route
app.use("/api/todos", todosRoute);","marks":[{"type":"code"}],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"Notice how we’re using ","marks":[],"data":{}},{"nodeType":"text","value":"/api/todos","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" in the route middleware and then passing the ","marks":[],"data":{}},{"nodeType":"text","value":"todosRoute","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" that we imported. All the routes that we define in the ","marks":[],"data":{}},{"nodeType":"text","value":"Routes/todos.js","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" will be based on ","marks":[],"data":{}},{"nodeType":"text","value":"/api/todos","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":". So routes referencing ","marks":[],"data":{}},{"nodeType":"text","value":""/"","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" in ","marks":[],"data":{}},{"nodeType":"text","value":"Routes/todos.js","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" will go to ","marks":[],"data":{}},{"nodeType":"text","value":"/api/todos (localhost:8080/api/todos)","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":".","marks":[],"data":{}}],"data":{}},{"nodeType":"heading-2","content":[{"nodeType":"text","value":"Postman","marks":[],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"Open up postman. Postman will help us test HTTP verbs that aren’t supported by browsers by default. Now in Postman’s url bar go to ","marks":[],"data":{}},{"nodeType":"text","value":"localhost:8080/api/todos","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":", make sure the HTTP verb is set to ","marks":[],"data":{}},{"nodeType":"text","value":"GET","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" and press send. You should see something like this.","marks":[],"data":{}}],"data":{}},{"nodeType":"embedded-asset-block","content":[],"data":{"target":{"sys":{"id":"2LjmCJ4VdzKy8OadwrI9KI","type":"Link","linkType":"Asset"}}}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"Let’s actually get some real todo data up in here. First let’s create a post method.","marks":[],"data":{}}],"data":{}},{"nodeType":"heading-3","content":[{"nodeType":"text","value":"Routes/Todos.Js","marks":[],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"router.post('/', function(req, res, next) {
    var todo = new Todo(req.body);
    todo.save(function(err, todo) {
        if (err) return next(err);
        res.status(201);
        res.json({message: "Todo Created!", todo: todo});
    });});","marks":[{"type":"code"}],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"Now let’s add our first todo! Change the HTTP Verb from ","marks":[],"data":{}},{"nodeType":"text","value":"GET","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" to ","marks":[],"data":{}},{"nodeType":"text","value":"POST","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":". Click the ","marks":[],"data":{}},{"nodeType":"text","value":"body","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" tab, select the ","marks":[],"data":{}},{"nodeType":"text","value":"raw","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" radio button and select ","marks":[],"data":{}},{"nodeType":"text","value":"JSON(application/json)","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" from the dropdown. Let’s set ","marks":[],"data":{}},{"nodeType":"text","value":"Mow the lawn","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" as our first todo. Since our ","marks":[],"data":{}},{"nodeType":"text","value":"Todo Model","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" has default fields for the creation date and status, we only need to supply the title field. In the textarea below the radio buttons enter a json object for the todo like so.","marks":[],"data":{}}],"data":{}},{"nodeType":"embedded-asset-block","content":[],"data":{"target":{"sys":{"id":"1YBVPaVkv7OGEWKsI89jiV","type":"Link","linkType":"Asset"}}}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"Add another todo, ","marks":[],"data":{}},{"nodeType":"text","value":"Get ","marks":[{"type":"code"}],"data":{}},{"nodeType":"text","value":"a haircut","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" just for kicks.","marks":[],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"Let’s change our ","marks":[],"data":{}},{"nodeType":"text","value":"get","marks":[{"type":"code"}],"data":{}},{"nodeType":"text","value":" ","marks":[],"data":{}},{"nodeType":"text","value":"all todos","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" method to retrieve all of our todos.","marks":[],"data":{}}],"data":{}},{"nodeType":"heading-3","content":[{"nodeType":"text","value":"Routes/Todos.Js","marks":[],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"router.get('/', function(req, res, next) {
    Todo.find(function(err, todos){
        if (err) return next(err);
        res.json(todos);
    });});","marks":[{"type":"code"}],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"Test with postman, change HTTP verb back to ","marks":[],"data":{}},{"nodeType":"text","value":"GET","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":", make sure url is ","marks":[],"data":{}},{"nodeType":"text","value":"localhost:8080/api/todos","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" and press send. We should get back two total todos: ","marks":[],"data":{}},{"nodeType":"text","value":"Mow the lawn","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" and ","marks":[],"data":{}},{"nodeType":"text","value":"Get a haircut","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":".","marks":[],"data":{}}],"data":{}},{"nodeType":"heading-2","content":[{"nodeType":"text","value":"Get Single Todo","marks":[],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"We have three more actions to finish. All three will use the ","marks":[],"data":{}},{"nodeType":"text","value":"id","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" params. Thankfully, Mongo automatically creates an ","marks":[],"data":{}},{"nodeType":"text","value":"id","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" field for each todo document of type ","marks":[],"data":{}},{"nodeType":"text","value":"ObjectId","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":".","marks":[],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"router.get('/:id', function(req, res, next) {
    var id = mongoose.Types.ObjectId(req.params.id); // cast id param to ObjectId
    Todo.findById(id, function (err, todo) {
        if (err) return next(err);
        if (!todo) { err = new Error("Todo Not Found"); err.status = 404; return next(err); } // if todo doesn't exist, set err
        res.json(todo);
    });});","marks":[{"type":"code"}],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"In Postman, copy the ","marks":[],"data":{}},{"nodeType":"text","value":"id","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" value for ","marks":[],"data":{}},{"nodeType":"text","value":"Mow the lawn","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" and append it to the url to check.","marks":[],"data":{}}],"data":{}},{"nodeType":"heading-2","content":[{"nodeType":"text","value":"Update Todo","marks":[],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"We’ll use the ","marks":[],"data":{}},{"nodeType":"text","value":"PUT","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" method to update our todos. Within that we’ll use the ","marks":[],"data":{}},{"nodeType":"text","value":"findByIdAndUpdate","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" mongoose method. This will allow us to return the new todo in the response with the use of the ","marks":[],"data":{}},{"nodeType":"text","value":"{ new: true }","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" parameter. We’ll also set the ","marks":[],"data":{}},{"nodeType":"text","value":"validators","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" to true so users can’t enter a null todo.","marks":[],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"router.put('/:id', function (req, res, next) {
    Todo.findByIdAndUpdate(req.params.id, req.body, { new: true, runValidators: true }, function (err, todo) {
        if (err) return next(err);
        if (!todo) { err = new Error("Todo Not Found"); err.status = 404; return next(err); }
        res.json({ message: "Todo updated!", todo: todo });
    });});","marks":[{"type":"code"}],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"Change the HTTP verb in Postman to ","marks":[],"data":{}},{"nodeType":"text","value":"PUT","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":". Make sure url is at ","marks":[],"data":{}},{"nodeType":"text","value":"localhost:8080/api/todos/[id-of-mow-the-lawn]","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":". Now just like the ","marks":[],"data":{}},{"nodeType":"text","value":"post","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" method we’re going to go the body tab, select raw and select JSON(application/json) from the dropdown. Let’s update ","marks":[],"data":{}},{"nodeType":"text","value":"Mow the lawn","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":"’s status to true, meaning we’ve completed the todo. In the textarea, type a json object with status equaling true.","marks":[],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"{
    "status": true
}","marks":[{"type":"code"}],"data":{}}],"data":{}},{"nodeType":"heading-2","content":[{"nodeType":"text","value":"Delete Todo","marks":[],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"Finally, for our last HTTP operation, we’ll set up a delete route. We can use ","marks":[],"data":{}},{"nodeType":"text","value":"mongoose’s","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":" remove method to achieve deleting a todo from the database.","marks":[],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"router.delete('/:id', function (req, res, next) {
    Todo.findById(req.params.id, function (err, todo) {
        if (err) return next(err);
        if (!todo) { err = new Error("Todo Not Found"); err.status = 404; return next(err); }
        todo.remove(function(err){
            if (err) return next(err);
            res.json({message: "Todo deleted!"});
        })
    });});","marks":[{"type":"code"}],"data":{}}],"data":{}},{"nodeType":"heading-2","content":[{"nodeType":"text","value":"Conclusion","marks":[],"data":{}}],"data":{}},{"nodeType":"paragraph","content":[{"nodeType":"text","value":"And just like that, we have a RESTful API up and running. In order for other domains to interact and fetch data from this API we will have to enable the ","marks":[],"data":{}},{"nodeType":"hyperlink","content":[{"nodeType":"text","value":"cors","marks":[],"data":{}}],"data":{"uri":"https://github.com/expressjs/cors"}},{"nodeType":"text","value":" package. CORS stands for ","marks":[],"data":{}},{"nodeType":"text","value":"Cross-Origin Resource Sharing","marks":[{"type":"bold"}],"data":{}},{"nodeType":"text","value":". Domains, by default don’t allow other domains to fetch their data, we can remedy this by enabling cors to specific routes or all routes. We will tackle this in a future blog post with React.js.","marks":[],"data":{}}],"data":{}}]}
